import React, { useState } from "react";
import Search from "./Search";

function Home({ movieData, seriesData }) {
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = (query) => {
    const filteredResults = [...movieData, ...seriesData].filter(
      (item) =>
        item.title.toLowerCase().includes(query.toLowerCase()) ||
        item.name.toLowerCase().includes(query.toLowerCase())
    );
    setSearchResults(filteredResults);
  };

  return (
    <div className="home-container">
      <h2 className="glowing-text">Welcome to MovieFlix!</h2>
      <p className="glowing-text">
        Discover the latest movies and top-rated TV series.
      </p>
      <Search style={{ width: "80%", fontSize: "1.2rem" }} onSearch={handleSearch} />
      <div className="search-results">
        {searchResults.map((item) => (
          <p key={item.id}>{item.title || item.name}</p>
        ))}
      </div>
    </div>
  );
}

export default Home;
