import React from "react";
import "./index.css";

function Body({ data }) {
  const Types = [
    "Action",
    "Adventure",
    "Animation",
    "Comedy",
    "Crime",
    "Documentary",
    "Drama",
    "Family",
    "Fantasy",
    "History",
    "Horror",
    "Music",
    "Mystery",
    "Romance",
    "Science fiction",
    "Tv Movie",
    "Thriller",
    "War",
    "Western",
  ];
  consol

  function Movies() {
    if (!Array.isArray(data)) {
      return <p>No movie data available.</p>;
    }

    return data.map((movie) => (
      <div class="card" style={{ width: "18rem" }} key={movie.id}>
        <img
          class="card-img-top"
          src={`https://via.placeholder.com/100x180?text=${movie.title}`}
          alt={movie.title}
        />
        <div class="card-body">
          <p class="card-text">{movie.overview}</p>
        </div>
      </div>
    ));
  }

  return (
    <main>
      <div className="button-container">
        {Types.map((type) => (
          <button className="my-button" key={type}>
            {type}
          </button>
        ))}
      </div>
      <div className="movie-container">
        <Movies />
      </div>
    </main>
  );
}

export default Body;
