import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom"; // Import BrowserRouter
import "./index.css";
import Movie from "./Movie";
import Navbar from "./Navbar";
import Series from "./Series";

function App() {
  const [data, setData] = useState([]);
  const apiKey = "702e82f205abc7fb5cb4651aa49efc8b";

  const url = `https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}`;
  useEffect(() => {
    fetch(url)
      .then((res) => res.json())
      .then((responseData) => {
        const movieArray = responseData.results || [];
        setData(movieArray);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  return (
    <Router>
      {" "}
      {/* Wrap your app with the Router component */}
      <div className="app-container">
        <Navbar />
        <Routes>
          <Route path="/" element={<Movie Data={data} />} />
          <Route path="/movies" element={<Movie Data={data} />} />
          <Route path="/series" element={<Series Data={data} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
