import React, { useState, useEffect } from "react";
import Search from "./Search";

function Home({ Data }) {
  const [searchResult, setSearchResult] = useState(null);

  useEffect(() => {
    if (searchResult) {
      const apiKey = "702e82f205abc7fb5cb4651aa49efc8b";
      const movieUrl = `https://api.themoviedb.org/3/movie/${searchResult.id}?api_key=${apiKey}`;

      fetch(movieUrl)
        .then((res) => res.json())
        .then((movieData) => {
          setSearchResult(movieData);
        })
        .catch((error) => {
          console.error("Error fetching movie data:", error);
        });
    }
  }, [searchResult]);

  function handleSearch(query) {
    const apiKey = "702e82f205abc7fb5cb4651aa49efc8b";
    const searchUrl = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${query}`;

    fetch(searchUrl)
      .then((res) => res.json())
      .then((responseData) => {
        const movie = responseData.results[0];
        setSearchResult(movie);
      })
      .catch((error) => {
        console.error("Error searching:", error);
      });
  }

  return (
    <div className="home-container">
      <h2 className="glowing-text">Welcome to MovieFlix!</h2>
      <p className="glowing-text">
        Discover the latest movies and top-rated TV series.
      </p>
      <Search onSearch={handleSearch} />
      {searchResult && (
        <div>
          <h2>Search Results</h2>
          <p>Title: {searchResult.title}</p>
          <p>Release Date: {searchResult.release_date}</p>
          <p>Rating: {searchResult.vote_average}</p>
        </div>
      )}
    </div>
  );
}

export default Home;
