import React, { useState, useEffect } from "react";

function Search() {
  const [movieData, setMovieData] = useState([]);
  const [seriesData, setSeriesData] = useState([]);

  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetch(movieUrl)
      .then((res) => res.json())
      .then((responseData) => {
        const movieArray = responseData.results || [];
        setMovieData(movieArray);
      })
      .catch((error) => {
        console.error("Error fetching movie data:", error);
      });

    fetch(seriesUrl)
      .then((res) => res.json())
      .then((responseData) => {
        const seriesArray = responseData.results || [];
        setSeriesData(seriesArray);
      })
      .catch((error) => {
        console.error("Error fetching series data:", error);
      });
  }, []);

  function handleInputChange(event) {
    setSearchTerm(event.target.value);
  }

  function handleSearch() {
    const filteredMovies = movieData.filter((movie) => {
      return movie.title.toLowerCase().includes(searchTerm.toLowerCase());
    });

    const filteredSeries = seriesData.filter((series) => {
      return series.name.toLowerCase().includes(searchTerm.toLowerCase());
    });

    setMovieData(filteredMovies);
    setSeriesData(filteredSeries);
  }

  return (
    <div className="search-container">
      <div className="input-group search-input-container">
        <input
          type="search"
          className="form-control search-input"
          placeholder="Search"
          aria-label="Search"
          aria-describedby="search-addon"
          value={searchTerm}
          onChange={handleInputChange}
        />
        <button
          type="submit"
          className="btn btn-primary"
          onClick={handleSearch}
        >
          Search
        </button>
      </div>
    </div>
  );
}

export default Search;
