import React, { useState } from "react";
import Search from "./Search";
import Movie from "./Movie";
import Series from "./Series";

function Home({ Data }) {
  const [searchResults, setSearchResults] = useState([]);
  const [searchType, setSearchType] = useState(""); // "movie" or "series"

  const handleSearch = (query) => {
    if (searchType === "movie") {
      const results = Data.filter(
        (item) =>
          item.title.toLowerCase().includes(query.toLowerCase()) ||
          item.original_title.toLowerCase().includes(query.toLowerCase())
      );
      setSearchResults(results);
    } else if (searchType === "series") {
      const results = Data.filter((item) =>
        item.name.toLowerCase().includes(query.toLowerCase())
      );
      setSearchResults(results);
    }
  };

  return (
    <div className="home-container">
      <h2 className="glowing-text">Welcome to MovieFlix!</h2>
      <p className="glowing-text">
        Discover the latest movies and top-rated TV series.
      </p>
      <Search searchData={Data} onSearch={handleSearch} />
      {searchType === "movie" ? (
        <Movie Data={searchResults} />
      ) : searchType === "series" ? (
        <Series Data={searchResults} />
      ) : (
        <div></div>
      )}
    </div>
  );
}

export default Home;
