import React, { useState } from "react";
import Search from "./Search";
import Movie from "./Movie";
import Series from "./Series";

function Home({ Data }) {
  const [searchResults, setSearchResults] = useState([]);
  const [searchType, setSearchType] = useState(""); // "movie" or "series"

  const handleSearch = async (query, type) => {
    setSearchType(type);
    try {
      const apiKey = "702e82f205abc7fb5cb4651aa49efc8b";
      let searchUrl = "";

      if (type === "movie") {
        searchUrl = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${query}`;
      } else if (type === "series") {
        searchUrl = `https://api.themoviedb.org/3/search/tv?api_key=${apiKey}&query=${query}`;
      }

      const response = await fetch(searchUrl);
      const responseData = await response.json();

      const results = responseData.results || [];
      setSearchResults(results);
    } catch (error) {
      console.error("Error searching:", error);
    }
  };

  return (
    <div className="home-container">
      <h2 className="glowing-text">Welcome to MovieFlix!</h2>
      <p className="glowing-text">
        Discover the latest movies and top-rated TV series.
      </p>
      <Search searchData={Data} onSearch={handleSearch} />
      {searchType === "movie" ? (
        <Movie Data={searchResults} />
      ) : searchType === "series" ? (
        <Series Data={searchResults} />
      ) : (
        <div></div>
      )}
    </div>
  );
}

export default Home;
